import React, { useState, useEffect } from 'react';
import './App.css';
import ServiceList from './ServiceList';
import ServiceForm from './ServiceForm';

const App = () => {
    const [services, setServices] = useState([]);
    const [newService, setNewService] = useState({ name: '', description: '', price: '' });
    const [editService, setEditService] = useState(null);

    useEffect(() => {
        setServices([
            { id: 1, name: 'General Checkup', description: 'A general health checkup', price: 50 },
            { id: 2, name: 'Blood Test', description: 'Basic blood test', price: 30 },
        ]);
    }, []);

    const handleInputChange = (e) => {
        const { name, value } = e.target;
        if (editService) {
            setEditService({ ...editService, [name]: value });
        } else {
            setNewService({ ...newService, [name]: value });
        }
    };

    const handleAddService = () => {
        if (newService.name && newService.description && newService.price) {
            setServices([...services, { ...newService, id: Date.now() }]);
            setNewService({ name: '', description: '', price: '' });
        }
    };

    const handleUpdateService = () => {
        const updatedServices = services.map((service) =>
            service.id === editService.id ? editService : service
        );
        setServices(updatedServices);
        setEditService(null);
    };

    const handleDeleteService = (id) => {
        const updatedServices = services.filter((service) => service.id !== id);
        setServices(updatedServices);
    };

    return (
        <div className="App">
            <h1>Healthcare Services</h1>
            <ServiceForm
                service={editService || newService}
                onChange={handleInputChange}
                onSubmit={editService ? handleUpdateService : handleAddService}
                buttonText={editService ? "Update Service" : "Add Service"}
            />
            <ServiceList services={services} onEdit={setEditService} onDelete={handleDeleteService} />
        </div>
    );
};

export default App;
